// }


const Plus = () => {
    
    let [count , valueNew] = useState(0);
    
    const myFun = () => {
        valueNew(count++);
    }

  return (
    <>
    
      <div className="container">
        <h1 className="head"> {count} </h1>
        <button onClick = {myFun} className="btn">Click me</button>
      </div>
    </>
  );
};

export default Plus ;
