//use Effect

import React from "react";
import { useState } from "react";

const Context = () => {
  let [num, setNum] = useState(0);

  return (
    <>
      <h1>{Num}</h1>
      <button onClick={() => {
        setNum(num++);
      }}></button>
    </>
  )
}

export default Context;
