import React, { useState } from "react";

const Plus = () => {

  const [value , newValue] = useState(0);
  return (
    <>
      <div className="centre_div">
        
        <h1>{value}</h1>
        <button className="btn1">Increment</button>
        <button className="btn2">Decrement</button>

      </div>
    </>
  );
};

export default Plus;
