import React, { useState } from "react";

const Plus = () => {
  const [Name, newName] = useState("");
  const [pswrd, newpswrd] = useState("");

  const [fullname, getFullName] = useState("");
  const [fullpswrd, getFullPswrd] = useState("");

  const inputField = (event) => {
    console.log(event.target.value);
    newName(Name);
  };

  const inputPswrd = (event) => {
    console.log(event.target.value);
    newpswrd(pswrd);
  };

  const submitData = (event) => {
    event.preventDefault();
    getFullName(Name);
    getFullPswrd(pswrd);
  };

  return (
    <>
      <form onSubmit={submitData}>
        <div className="container">
          <h1>Hello {fullname}</h1>
          <input type="text" onChange={inputField} value= {Name} />
          <br />

          <h2>Your Password is {fullpswrd}</h2>
          <input type="text" onChange={inputPswrd} value= {pswrd} />
          <br />
          <br />

          <button type = "submit">Click Me </button>
        </div>
      </form>
    </>
  );
};

export default Plus;
