import React, { useState } from "react";
import "../node_modules/bootstrap/dist/css/bootstrap.min.css";

const Form1 = () => {

  let [newNum , setNewNum] = useState(0);

  let updateIncre = () => {
    setNewNum(newNum++);
  }

  let updateDecre = () => {
    (newNum >= 0) ? setNewNum(newNum--) : alert("value reached to zero");
  }

  return(
    <>
      <div className = "container w-50">
        <h1 className = "display-2" style = {{fontFamily: "arial" , fontWeight: "bolder"}}>{newNum}</h1><br/>
        <button className = "btn btn-outline-info " onClick = {updateIncre}>Increase by 1</button>
        <button className = "btn btn-outline-success mx-5" onClick = {updateDecre}>Decrease by 1</button>
      </div>
    </>
  );
}

export default Form1;
