import React, { useState } from "react";

const Plus = () => {
  const [fullName , setFullName] = useState({
    fname : "",
    lname : ""
  });
  return (
    <>
      <div className="container">
      <form>
        <h1>Hello {fullName.fname} {fullName.lname}</h1>
        <hr />
        <input type="text" onChange = {inputField} value = {fullName.fname} placeholder="Enter Your First Name" />
        <br />
        <br />
        <input type="text" onChange = {inputField} value = {fullName.lname} placeholder="Enter Your Last Name" />
        <br />
        <br />
        <button>Click Me</button>
        </form>
      </div>
    </>
  );
};

export default Plus;
