import React, { useState } from "react";

const Plus = () => {

  let dispTime = new Date().toLocaleTimeString();
  let [oldTime, currentTime] = useState(dispTime);

  setInterval(getOutput() , 1000);

  function getOutput() {
    currentTime(dispTime);
  }


  return(
    <>
      <h1>{dispTime}</h1>
    </>
  )

}

export default Plus;
