import React, { useState } from "react";

const Plus = () => {

  let [details , newDetails] = useState();

  const inputField = (event) => {
    console.log(event.target.value)
    console.log(event.target.name)
  }

  const sendData = ( event ) => {
    event.preventDefault();
    alert("Form Submitted");
  }
  return(
    <>
      <div className = "container">
        <h1>Hello </h1><br/>
        
        <form onSubmit = {sendData}>
        <label for = "Firstname">First Name : </label>
        <input type = "text" name = "fname" onChange = {inputField} placeholder = "Enter Your First Name"/><br/><br/>

        <label for = "Lastname">Last Name : </label>
        <input type = "text" name = "lname" onChange = {inputField} placeholder = "Enter Your Last Name"/><br/><br/>

        <label for = "Fathername">Father Name : </label>
        <input type = "text" name = "fathername" onChange = {inputField} placeholder = "Enter Your Father Name"/><br/><br/>

        <label for = "Mothername">Mother Name : </label>
        <input type = "text" name = "mothername" onChange = {inputField} placeholder = "Enter Your Mother Name"/><br/><br/>
        
        <label for = "Address">Address : </label>
        <input type = "text" name = "add" onChange = {inputField} placeholder = "Enter Your Address"/><br/><br/>
        
        <label for = "e-mail">E-mail : </label>
        <input type = "e-mail" name = "email" onChange = {inputField} placeholder = "Enter Your E-mail"/><br/><br/>
        
        <label for = "contact">Contact : </label>
        <input type = "num" name = "num" onChange = {inputField} placeholder = "Enter Your Contact"/><br/><br/>
 
        <input type = "submit" value = "Click to Submit"/>
        
        </form>
      </div>
    </>
  );
}


export default Plus;
