import React, { useState } from "react";
import "../node_modules/bootstrap/dist/css/bootstrap.min.css";

const IncreNumber = () => {
  let [number, setNumber] = useState(0);
  let increNum = () => setNumber(number++);
  let decreNum = () => number >= 0 ? setNumber(number--) : alert("reached limit");

  return (
    <>
      <div className="container w-25 mx-auto my-5 p-5">
        <h1
          className="display-1 text-center"
          style={{ fontFamily: "Montserrat" }}
        >
          {number}
        </h1>
        <div className="row">
          <div className="col-10 mx-auto ">
            <button onClick={increNum} className="btn btn-outline-success">
              Increment
            </button>
            <button onClick={decreNum} className="btn btn-outline-danger">
              Decerement
            </button>
          </div>
        </div>
      </div>
    </>
  );
};

export default IncreNumber;
