import React, { useState } from "react";
import NextWeekIcon from '@material-ui/icons/NextWeek';
import "../node_modules/bootstrap/dist/css/bootstrap.min.css";

const Form1 = () => {

  let [newValue , setValue] = useState("");
  let [store , newStore] = useState([]);

  const getChange = (event) => {
    store = event.target.value;
    setValue(store);
  }
  
  const submitData = (event) => {
    newStore.push(newValue);
  }

    return (
      <>
        <div className = "container">
          
          <h1 className = "text-capitalize">ToDoApp {newValue}</h1>
          <input type = "text" onChange = {getChange} className= "py-1 px-3 my-3" placeholder = "Add Item"/>
          <button className = "btn btn-outline-info mx-4" onClick = {submitData}>
            <NextWeekIcon/> Save Item
          </button>

          {store.map((value) => {
            return <li>value</li>
          })}

        </div>
        </>
    );
}

export default Form1;
