import React from "react";
import { useHistory, useLocation, useParams } from "react-router";
import "../node_modules/bootstrap/dist/css/bootstrap.min.css";

const RouterContact = () => {

    let { fname } = useParams();
    let getLocate = useLocation();
    let history = useHistory();
    console.log(history)
    console.log(getLocate);

    return (
        <>
            <h1>Your name is {fname} </h1><br />
            <h2>Your location is {getLocate.pathname}</h2>
            {(getLocate.pathname == "/contact/rudra" ? <button onClick={() => history.goBack()} className="btn btn-info">Hello Rudra</button> : null)}
        </>
    );
}

export default RouterContact;
