import React, { useState } from "react";
import "../node_modules/bootstrap/dist/css/bootstrap.min.css";

let Form1 = () => {

  let [data , newData] =  useState("");
  let [showData , setData] = useState([]);

  const update = (event) => {
    newData(event.target.value);
  }

  const saveData = () => {
    setData((preval) => {
      return [...preval , data];
    })
    newData("");
  }

  return(
    <>
      <section>
        <div className = "container w-50 my-5">
          <div className = "row">
            <div className = "col-md-12 col-10 ">
  
           <label for="inputPassword" class="col-sm-2 col-form-label">Add Item</label>
             <div class="col-sm-10">
           <input type="text" class="form-control px-3" id="inputPassword" placeholder = "Add your Item" value = {data} onChange = {update}/>
           <button className = "btn btn-outline-success mt-3" onClick = {saveData}>Save Data</button>
            </div>
  
            </div>
          </div>
        </div>
      </section>
      <div className = "container w-50 mx-auto">
        {showData.map((values, index) => {
          return <li className = "display-6 mb-3 mx-auto text-capitalize" style = {{listStyleType :"none"}}><svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" class="bi bi-trash-fill" viewBox="0 0 16 16">
  <path d="M2.5 1a1 1 0 0 0-1 1v1a1 1 0 0 0 1 1H3v9a2 2 0 0 0 2 2h6a2 2 0 0 0 2-2V4h.5a1 1 0 0 0 1-1V2a1 1 0 0 0-1-1H10a1 1 0 0 0-1-1H7a1 1 0 0 0-1 1H2.5zm3 4a.5.5 0 0 1 .5.5v7a.5.5 0 0 1-1 0v-7a.5.5 0 0 1 .5-.5zM8 5a.5.5 0 0 1 .5.5v7a.5.5 0 0 1-1 0v-7A.5.5 0 0 1 8 5zm3 .5v7a.5.5 0 0 1-1 0v-7a.5.5 0 0 1 1 0z"/>
</svg>  {values}</li>
        })}
      </div>
    </>
  );
}

export default Form1;
