import React, { useState } from "react";
import ToDoList1 from "./ToDoList1";
import TextField from "@material-ui/core/TextField";
import Button from "@material-ui/core/Button";
import Tooltip from "@material-ui/core/Tooltip";


const ToDoList = () => {
  let [Data, newData] = useState("");
  let [printData, newprintData] = useState([]);

  const inputData = (event) => {
    newData(event.target.value);
    console.log(Data);
  };
  
  const sendData = () => {
    newprintData((preVal) => {
      return [...preVal, Data];
    });
    newData("");
  };

  return (
    <>
      <div className="center_div">
        <h1>ToDo List</h1>
        <TextField
          value={Data}
          id="outlined-basic"
          label="Your Item"
          placeholder="Add a Item"
          variant="outlined"
          onChange={inputData}
        />
        <br />
        <br />
        <Tooltip disableFocusListener title="Save Item">
          <Button
            className="btn1"
            onClick={sendData}
            variant="contained"
            color="secondary"
          >
            Save
          </Button>
        </Tooltip>
        {printData.map((elem) => {
            return <ToDoList1 text = {elem}/>
        })}
        

</div>
</>
)
  }

  export default ToDoList;
