import React from "react";
import { useState } from "react";

const Router = () => {

    let [num, newNum] = useState(0);
    
    const get = () => {
       return newNum(num++);
    }

    return (
        <>
            <h1>{num}</h1>
            <button onClick= {get}>Increase Value by 1</button>
        </>
    );

}

export default Router;
