import React, { useState } from "react";
import "../node_modules/bootstrap/dist/css/bootstrap.min.css";

const Form1 = () => {
  let [num, newNum] = useState(0);
  const updateNum = () => newNum(num++);
  const decreNum = () => (num >= 0 ? newNum(num--) : alert("reached to zero")); //setting condition using the ternary operator

  return (
    <>
      <div ClassName="container">
        <h1>{num}</h1>
        <button class="btn btn-outline-success" onClick={updateNum}>
          next
        </button>
        <button class="btn btn-outline-primary" onClick={decreNum}>
          previous
        </button>
      </div>
    </>
  );
};

export default Form1;
