import React, { useState } from "react";

//building a to do app

const Plus = () => {
  let [task , newTask] = useState("");

  let txtArea = ( event ) => {
    event.target.value
  }
  
  return (
    <>
      <div className = "box">
        
        <h1>Enter Your Task Here</h1>
        <textarea value = {task} onChange = {txtArea} rows = "10" cols = "50" placeholder = "Enter Your Task"></textarea><br/><br/>
        <button>Save</button>

      </div>
    </>
  )
}

export default Plus;
