import React from "react";
import { useEffect } from "react";
import { useState } from "react";

const Router = () => {

    let [num, newNum] = useState(0);
    useEffect(() => {
        alert("hello world this is the react useEffect operation")
    })
    return (
        <>
            <h1>{num}</h1>
            <button onClick=    {() => newNum(num++)}>Increase Value by 1</button>
        </>
    );

}

export default Router;
