import React, { useState } from "react";
import ArrowForwardIcon from '@material-ui/icons/ArrowForward';
import "../node_modules/bootstrap/dist/css/bootstrap.min.css";

const Form1 = () => {

  let [store , newStore] = useState("");
  let [itemStore, newItemStore] = useState([]);

  const updateData = (event) => {
    newStore(event.target.value);
  }

  const submitData = () => {  
    newItemStore.push(store);
    newStore("");
  }

  
  return(
    <>
      <div className = "container py-3">
        <h1>ToDoList</h1>
        <input type = "text" value = {store} onChange = {updateData} placeholder = "Add Item" className = "px-2"/>
        <button className = "btn btn-outline-info mx-3" onClick = {submitData}><ArrowForwardIcon/> Save</button>
      </div>
    </>
  );
}

export default Form1;
