import React, { useState } from "react";

const Plus = () => {

  const [inputData , newinputData] = useState("");
  const [value , newValue] = useState("");

  const getData = (event) => {
    newinputData(event.target.value);
  }

  const sendData = () => {
    value(inputData);
  }



  return (
    <>
      <div className = "container">
        
        <h1>ToDo Task</h1>
         <input type = "text" onChange = {getData} value = {inputData} placeholder = "Enter Your Task"/><br/><br/>
        <button onClick = {sendData}>Save Task</button>

        {value}

      </div>
    </>
  );
}

export default Plus;
