import React, { useState } from "react";
import ToDoList from "./ToDoList";

const Plus = () => {

  const [inputData , newinputData] = useState("");
  const [value , newValue] = useState([]);

  const getData = (event) => {
    newinputData(event.target.value);
  }

  const sendData = () => {
    newValue((preValue) => {
      return [...preValue , inputData]
    });
    newinputData("");
  }

  const deleteData = () => {
    console.log("deleted");
  }



  return (
    <>
      <div className = "container">
        
        <h1>ToDo Task</h1>
         <input type = "text" onChange = {getData} value = {inputData} placeholder = "Enter Your Task"/><br/><br/>
        <button onClick = {sendData}>Save Task</button><br/>

      </div>
      
      {value.map((elem , index) => {
          return <ToDoList id = {index} key = {index} text = {elem} onSelect = {deleteData}/>
        })}
    </>
  );
}

export default Plus;
