import React, { useState } from "react";
import "../node_modules/bootstrap/dist/css/bootstrap.min.css";
import ForwardSharpIcon from "@material-ui/icons/ForwardSharp";

const KeepNote = () => {

  let [data, setData] = useState({
    title : "",
    content : ""
  });

  return (
    <>
      <div className="container w-50 mx-auto py-5">
        <div className="row">
          <div className="col-md-10 col-12">
            <input type="text" placeholder="Title" className="form-control" value = {data.title} name = {title}/>
            <textarea
              placeholder="Keep a Note.."
              className="form-control mt-1"
              rows=""
              column=""
              value = {data.content}
              name = {content}
            />
            <button className="btn btn-outline-info mt-1 kNoteBtn">
              Save a Note <ForwardSharpIcon />
            </button>
          </div>
        </div>
      </div>
    </>
  );
};

export default KeepNote;
