import React, { useState } from "react";

const Plus = () => {

  const [data , setdata] = useState("");
  const [newValue , setnewValue] = useState("");

  const inputData = (event) => {
    setdata(event.target.value);
  }

  const sendData = () => {
    setnewValue(data);
  }

  return (
    <>
      <div className="box">
        <h1>ToDo Lsit</h1>
        <input type="text" onChange = {inputData} value = {data} placeholder="Add a Item" />
        <button onClick = {sendData}>+</button>{newValue}
      </div>
    </>
  );
};

export default Plus;
