import React, { useState } from "react";

const CloneNote = () => {

    let [data , setData] = useState({
        title : "",
        content : ""
    });
  return (
    <>
      <section>
        <div className="container w-50 Note pb-5 mt-4">
          <div className="row">
            <div className="col-md-8 col-12 mx-auto mt-5">
              <input
                type="text"
                class="form-control"
                id="exampleFormControlInput1"
                placeholder="Title"
                title="Enter Title"
                value = {data.title}
                onChange = {inputData}
                name = {title}
              />
              <textarea
                class="form-control mt-1"
                id="exampleFormControlTextarea1"
                rows=""
                column=""
                placeholder="Save Your Note..."
                title="Save Your Note."
                value = {data.content}
                onChange = {inputData}
                name = {content}
              ></textarea>
              <button className="btn btn-outline-success mt-1">
                <svg
                  xmlns="http://www.w3.org/2000/svg"
                  width="16"
                  height="16"
                  fill="currentColor"
                  class="bi bi-trash-fill"
                  viewBox="0 0 16 16"
                >
                  <path d="M2.5 1a1 1 0 0 0-1 1v1a1 1 0 0 0 1 1H3v9a2 2 0 0 0 2 2h6a2 2 0 0 0 2-2V4h.5a1 1 0 0 0 1-1V2a1 1 0 0 0-1-1H10a1 1 0 0 0-1-1H7a1 1 0 0 0-1 1H2.5zm3 4a.5.5 0 0 1 .5.5v7a.5.5 0 0 1-1 0v-7a.5.5 0 0 1 .5-.5zM8 5a.5.5 0 0 1 .5.5v7a.5.5 0 0 1-1 0v-7A.5.5 0 0 1 8 5zm3 .5v7a.5.5 0 0 1-1 0v-7a.5.5 0 0 1 1 0z" />
                </svg>
                Save
              </button>
            </div>
          </div>
        </div>
      </section>
    </>
  );
};

export default CloneNote;
