import React, { useState } from "react";
import "../node_modules/bootstrap/dist/css/bootstrap.min.css";
import AddIcon from '@material-ui/icons/Add';

const KeepNote = () => {
  let [data , newData] = useState("");
  let [data1 , newData1] = useState("");
  let [dispData , setDispData] = useState([]);
  let [dispData1 , setDispData1] = useState([]);

  const inputData = (event) => {
    newData(event.target.value);
  }
  
  const inputData1 = (event) => {
    newData1(event.target.value);
  }

  const submitData = () => {
    setDispData((preValue) => {
      return [...preValue , data];
    })
    newData("");

  }

  return (
    <>
      <section className="container my-5 w-50 ">
        <div className="row ">
          <div className="col-md-10 col-12 mx-auto">
            <div class="mb-1">
              <input
                type="text"
                placeholder="Title"
                class="form-control"
                id="exampleFormControlInput1"
                value = {data}
                onChange = {inputData}
              />
            </div>
            <textarea
              class="form-control"
              id="exampleFormControlTextarea1"
              rows=""
              column=""
              placeholder="Save Your Note"
              value = {data1}
              onChange = {inputData1}
            ></textarea>
            <button type="button" class="btn btn-outline-info mt-1 mx-auto" onClick = {submitData}><AddIcon/></button>
          </div>
        </div>
      </section>

      {dispData}
      {dispData1}
    </>
  );
};

export default KeepNote;
