//use Context hook
// this react hoook is used when need to transfer the data to particular child by its parent so that it can only be accessible inside that particular child component

import React from "react";
import { createContext } from "react";
import Child1 from "./Child1";

let FirstName = createContext();

const Context = () => {


  return (
    <>
      <FirstName.Provider value={"tarun"}>
        <Child1 />
      </FirstName.Provider>
    </>
  );
}

export default Context;
export { FirstName };
