import React, { useState } from "react";
import "../node_modules/bootstrap/dist/css/bootstrap.min.css";

const Form1 = () => {

  let [newNum , setNewNum] = useState(0);

  let updateIncre = () => {
    setNewNum(newNum++);
  }

  let updateDecre = () => {
    (newNum >= 0) ? setNewNum(newNum--) : alert("value reached to zero");
  }

  return(
    <>
      <div className = "container w-50" >
      <div className = "row mx-auto" style = {{border: "2px solid"}}>
        <h1 className = "display-2" style = {{fontFamily: "arial" , fontWeight: "bolder"}}>{newNum}</h1><br/>
        <div className = "text-center text-capitalize col-md-6 col-12"><button className = "btn btn-outline-info " onClick = {updateIncre}>Increase by 1</button></div>
      </div>
      </div>
    </>
  );
}

export default Form1;
