import React, { useState } from "react";

//building a to do app

const Plus = () => {
  let [task , newTask] = useState("");

  let txtArea = ( event ) => {
    newTask(event.target.value);
  }

  return (
    <>
      <div className = "box">
        <form>
        <h1>Enter Your Task Here</h1>
        <textarea value = {task} onChange = {txtArea} rows = "10" cols = "50" placeholder = "Enter Your Task"></textarea><br/><br/>
        <button>Save</button>

        <div className = "box2">
          <h3>{task}</h3>
        </div>
        
        </form>
      </div>
    </>
  )
}

export default Plus;
