import React, { useState } from "react";


const Form1 = () => {

  let [num , newNum] = useState(0);

 const update = () => {
   return setInterval(()=> {
     return newNum++;
   }) }

  return(
    <>
      <div className = "container">
        <h1>{num}</h1>
        <button className = "btn btn-outline-info " onClick = {update}>incre</button>
      </div>
    </>
  );
}

export default Form1;
