import React from "react";
import { useState } from "react";

const RouterService = () => {
    let [data, setData] = useState("");
    let [picture , setPicture] = useState("");

    const inputData = (event) => {
        setData(event.target.value);
        setPicture(event.target.value)
    }

    return (
        <>
            <input type="text" className="text-center my-3" value={data} onChange={inputData} placeholder="Search Item" />
            <div className = "container w-50 mx-auto my-5"  style = {{height: "auto"}}>
                {(picture == "") ? null :<img src = {`https://source.unsplash.com//400x500/?${picture}`} alt = "actial image" className = "img-fluid"/>}
            </div>
        </>
    );
}

export default RouterService;
