import React, { useState } from "react";


const Plus = () => {
    const [count, newValue] = useState(0);
    
    function incre() {
        newValue(count++);
    }
    return (
    <>
      <div class="container">
        <h1>{count}</h1>
        <button onClick = {incre}>Click Me</button>
      </div>
    </>
  );
};

export default Plus;
