import React, { useState } from "react";

let Plus = () => {
  let [Name, newName] = useState("");
  let [pswrd, newpswrd] = useState("");

  let [fullname, getFullName] = useState();
  let [fullpswrd, getFullPswrd] = useState();

  let inputField = (event) => {
    console.log(event.target.value);
    newName(Name);
  };

  let inputPswrd = (event) => {
    console.log(event.target.value);
    newpswrd(pswrd);
  };

  let submitData = (event) => {
    event.preventDefault();
    getFullName(Name);
    getFullPswrd(pswrd);
  };

  return (
    <>
      <form onSubmit={submitData}>
        <div className="container">
          <h1>Hello {fullname}</h1>
          <input type="text" onChange={inputField} value={Name} />
          <br />

          <h2>Your Password is {fullpswrd}</h2>
          <input type="text" onChange={inputPswrd} value={pswrd} />
          <br />
          <br />

          <input type="submit" />
        </div>
      </form>
    </>
  );
};

export default Plus;
