import React from "react";
//this is the JSX element file


function Card(props) {
  return (
  <React.Fragment>
    <div className="cards">
    <h1>This is the 5 best webseries</h1>
      <div className="card">
        <img src={props.image} alt="This is the Random image" />
        <div className="data">
          
          <h2>A NetFlix Origninal Series</h2>
          
          <h3>{props.name}</h3>

          <a href={props.link} target="_blank">
          <button>Watch now</button>
          
          </a>
        </div>
      </div>
    </div>
  </React.Fragment>
  );
}

export default Card;
