import React, { useState } from "react";
import "../node_modules/bootstrap/dist/css/bootstrap.min.css";


const Form1 = () => {

  let [num , newNum] = useState(0);

  const updateNum = () => {
    newNum(num++);
  }

  const decreNum = () => {
    if(num > 0){
      newNum(num--);
    }
  }

  return(
    <>
      <div ClassName = "container">
        <h1>{num}</h1>
        <button class = "btn btn-outline-success" onClick = {updateNum}>next</button>
        <button class = "btn btn-outline-warning" onClick = {decreNum}>next</button>
      </div>
    </>
  );
}

export default Form1;
