import React from "react";

function Card( props ) {
  <div className="cards">
    <img src={props.image} />

    <div className="card">
      <h2>Amazon Prime Exclusive</h2>
      <h3>{props.name}</h3>

      <a href={props.link}>
        <button>Watch Now</button>
      </a>
    </div>
  </div>;
}

export default Card;
