import React, { useState } from "react";
import ToDoList from "./ToDoList";

const Plus = () => {
  let [inputData , newinputData] = useState("");
  let [setValue , newsetValue] = useState([]);

  const updateData = (event) => {
    newinputData(event.target.value);
  }

  const sendData = (event) => {
    newsetValue((preVal) => {
      return [...preVal , inputData];
    });
    newinputData("");
  }

  return (
    <>
      <div className="box">
        <h1>ToDo List</h1>
        <input type="text" value = {inputData} placeholder = "Add a item" onChange = {updateData}/>
        <button onClick = {sendData} >+</button><br/><br/>
        
        {setValue.map((elem)=> {
                return <ToDoList text = {elem}/>
            })}

      </div>
    </>
  );
};
export default Plus;
