import React from "react";

const Form1 = () => {

  let [num , newNum] = useState(0);

  const updateNum = () => {
    newNum(num++);
  }

  return(
    <>
      <div ClassName = "container">
        <h1>{num}</h1>
        <button onClick = {updateNum}>Start</button>
      </div>
    </>
  );
}

export default Form1;
