import React, { useState } from "react";

const Plus = () => {
  const [fullName , setFullName] = useState({
    fname : "",
    lname : ""
  });

  const inputField = ( event ) => {

    let storeValue = event.target.value;
    let storeName = event.target.name;

    setFullName((preValue) => {
      if(storeName === "Fname") {
        return{
          fname : storeValue,
          lname : preValue.lname
        }
      }
    })
  
  }

  const sendData = () => {
    alert("form Submitted");
  }

  return (
    <>
      <div className="container">
      <form onSubmit = {sendData}>

        <h1>Hello {fullName.fname} {fullName.lname}</h1>
        <hr />
        <input type="text" onChange = {inputField} value = {fullName.fname} name = "Fname" placeholder="Enter Your First Name" />
        <br />
        <br />
        <input type="text" onChange = {inputField} value = {fullName.lname} name = "Lname" placeholder="Enter Your Last Name" />
        <br />
        <br />
        <button>Click Me</button>

        </form>
      </div>
    </>
  );
};

export default Plus;
