import React, { useState } from "react";

const Plus = () => {

  let dispTime = new Date().toLocaleTimeString();
  let [oldTime, currentTime] = useState(dispTime);

  let ready = () => {
    dispTime = new Date().toLocaleTimeString();
    currentTime()
  }

  
  return(
    <>
      <h1>{dispTime}</h1>
      <button onClick = {ready}>Watch</button>
    </>
  )

}

export default Plus;
