import React, { useState } from "react";
import AddIcon from '@material-ui/icons/Add';

let Plus = () => {
let [ value , newValue] = useState(0);
const increNum = () => newValue(value++);
const decreNum = () => (value > 0) ? newValue(value--) : "Value reached to zero";

  return (
    <>
      <div className="centre_div">
        <h1>{value}</h1>
        <button onClick = {increNum}></button>
        <button onClick = {decreNum}></button>
      </div>
    </>
  );
};

export default Plus;
