import React, { useState } from "react";
import ToDoList from "./ToDoList";

const Plus = () => {

  const [data , setdata] = useState("");
  const [newValue , setnewValue] = useState([]);

  const inputData = (event) => {
    setdata(event.target.value);
  }

  const sendData = () => {
    setnewValue((preValue) => {
      return [...preValue , data];
    });
  }

  const deleteFun = () => {
    console.log("deleted")
  }

 
  return (
    <>
      <div className="box">
        <h1>ToDo Lsit</h1>
        <input type="text" onChange = {inputData} value = {data} placeholder="Add a Item" />
        <button onClick = {sendData}>+</button><br/>

        {newValue.map((elem , index) => {
          return <ToDoList text = {elem} onClick = {deleteFun} key = {index} id = {index} />
        })}

      </div>
    </>
  );
};

export default Plus;
