import React from "react";
import { useLocation, useParams } from "react-router";

const RouterContact = () => {

    let {fname} = useParams();
    let getLocate = useLocation();
    console.log(getLocate);

    return (
        <>
            <h1>Your name is {fname} </h1><br/>
            <h2>Your location is {getLocate.pathname}</h2>
            {getLocate == "/contact/rudra" ? (<button>Hello Rudra</button>) : null}
        </>
    );
}

export default RouterContact;
