import React, { useState } from "react";

const Plus = () => {
  let [details, newDetails] = useState({
    Fname: "",
    Lname: "",
    ["Father name"]: "",
    ["Mother name"]: "",
    Address: "",
    email: "",
    contact: "",
  });

  const inputField = (event) => {
    let value = event.target.value;
    let name = event.target.name;

    newDetails((preValue) => {
      if (name === "fname") {
        return {
          Fname: value,
          Lname: preValue.Lname,
          ["Father name"]: preValue["Father name"],
          ["Mother name"]: preValue["Mother name"],
          Address: preValue.Address,
          email: preValue.email,
          contact: preValue.contact,
        };
      } else if (name === "lname") {
        return {
          Fname: preValue.Fname,
          Lname: value,
          ["Father name"]: preValue["Father name"],
          ["Mother name"]: preValue["Mother name"],
          Address: preValue.Address,
          email: preValue.email,
          contact: preValue.contact,
        };
      } else if (name === "fathername") {
        return {
          Fname: preValue.Fname,
          Lname: preValue.Lname,
          ["Father name"]: value,
          ["Mother name"]: preValue["Mother name"],
          Address: preValue.Address,
          email: preValue.email,
          contact: preValue.contact,
        };
      }
      else if(name === "mothername") {
        return{
          Fname: preValue.Fname,
          Lname: preValue.Lname,
          ["Father name"]: preValue["Father name"],
          ["Mother name"]: value,
          Address: preValue.Address,
          email: preValue.email,
          contact: preValue.contact,

        }
      }
    });
  };

  const sendData = (event) => {
    event.preventDefault();
    alert("Form Submitted");
  };
  return (
    <>
      <div className="container">
        <h1>
          Hello {details.Fname} {details.Lname}
        </h1>
        <span>{details["Father name"]}</span>
        <br />

        <form onSubmit={sendData}>
          <label for="Firstname">First Name : </label>
          <input
            type="text"
            name="fname"
            onChange={inputField}
            value={details.Fname}
            placeholder="Enter Your First Name"
          />
          <br />
          <br />

          <label for="Lastname">Last Name : </label>
          <input
            type="text"
            name="lname"
            onChange={inputField}
            value={details.Lname}
            placeholder="Enter Your Last Name"
          />
          <br />
          <br />

          <label for="Fathername">Father Name : </label>
          <input
            type="text"
            name="fathername"
            onChange={inputField}
            value={details["Father name"]}
            placeholder="Enter Your Father Name"
          />
          <br />
          <br />

          <label for="Mothername">Mother Name : </label>
          <input
            type="text"
            name="mothername"
            onChange={inputField}
            value={details["Mother name"]}
            placeholder="Enter Your Mother Name"
          />
          <br />
          <br />

          <label for="Address">Address : </label>
          <input
            type="text"
            name="add"
            onChange={inputField}
            value={details.Address}
            placeholder="Enter Your Address"
          />
          <br />
          <br />

          <label for="e-mail">E-mail : </label>
          <input
            type="e-mail"
            name="email"
            onChange={inputField}
            value={details.email}
            placeholder="Enter Your E-mail"
          />
          <br />
          <br />

          <label for="contact">Contact : </label>
          <input
            type="num"
            name="num"
            onChange={inputField}
            value={details.contact}
            placeholder="Enter Your Contact"
          />
          <br />
          <br />

          <input type="submit" value="Click to Submit" />
        </form>
      </div>
    </>
  );
};

export default Plus;
