//use Effect

import React from "react";
import { useEffect } from "react";
import { useState } from "react";

const Context = () => {
  let [num, setNum] = useState(0);
  useEffect()

  return (
    <>
      <h1>{num}</h1>
      <button onClick={() => {
        setNum(num++);
      }}>increase by 1</button>
    </>
  )
}

export default Context;
