import React, { useState } from "react";
import "../node_modules/bootstrap/dist/css/bootstrap.min.css";
import DeleteIcon from '@material-ui/icons/Delete';

let Form1 = () => {
  let [data, newData] = useState("");
  let [showData, setData] = useState([]);

  const update = (event) => {
    newData(event.target.value);
  };

  const saveData = () => {
    setData((preval) => {
      return [...preval, data];
    });
    newData("");
  };

  return (
    <>
      <section>
        <div className="container w-50 my-5">
          <div className="row">
            <div className="col-md-12 col-10 ">
              <label for="inputPassword" class="col-sm-2 col-form-label">
                Add Item
              </label>
              <div class="col-sm-10">
                <input
                  type="text"
                  class="form-control px-3"
                  id="inputPassword"
                  placeholder="Add your Item"
                  value={data}
                  onChange={update}
                />
                <button
                  className="btn btn-outline-success mt-3"
                  onClick={saveData}
                >
                  Save Data
                </button>
              </div>
            </div>
          </div>
        </div>
      </section>
      <div className="container w-50 mx-auto display-6">
        {showData.map((values, index) => {
          return (
            <li
              className="mb-3 mx-auto text-capitalize"
              style={{ listStyleType: "none" }}
              id = {index}
            ><DeleteIcon className = "mr-5"/>  
              {values}
            </li>
          );
        })}
      </div>
    </>
  );
};

export default Form1;
