import React from "react";
import { useLocation, useParams } from "react-router";

const RouterContact = () => {

    let {fname} = useParams();
    let getLocate = useLocation().pathname;
    console.log(typeof getLocate);

    return (
        <>
            <h1>Your name is {fname}Current Location is {getLocate}</h1><br/>
            (getLocate == "/contact/rudra") ? <button className = "btn btn-info">Hello Rudra</button> : null;
        </>
    );
}

export default RouterContact;
