import React, { useState } from "react";

let Plus = () => {
  let [name , newName] = useState();
  let [FullName , setFullName] = useState("");

  let inputField = (event) => {
    console.log(newName(event.target.value));
  }

  let ready = () => {
    setFullName(name);
  }
  return (
    <>
      <div className = "container">
        <h1>Hello {FullName}</h1>
        <input type = "text" value = {name} onClick = {inputField}/>
        <button onClick = {ready}>Click Me</button>
      </div>
    </>
  );
}
export default Plus;
