import React, { useState } from "react";

const Plus = () => {

  let [Data , newData] = useState("");
  let [imageFile , newimageFile] = useState("https://image.flaticon.com/icons/png/512/3011/3011270.png");

const inputData = (event) => {
  newData(event.target.value);
}

const updatePicture = () => {
  newimageFile("https://joeschmoe.io/api/v1/random/" + {Data});
  console.log("https://joeschmoe.io/api/v1/random/" + "hello")
  alert("saved");
}


  return (
    <>
      <div className="page_div">
        
          <h1>Enter Your Name To get Amazing Avatar for you</h1>
          <input type="text" onChange = {inputData} value = {Data} placeholder="Enter Your Name" />
          <br /><br />
          <input type="button" onClick = {updatePicture} value="save" />
          <br /><br />
          <img
            src= {imageFile}
            width="200"
            height="200"
            alt="Picture has been removed by the user"
          />
        
      </div>
    </>
  );
};

export default Plus;
