import React, { useState } from "react";

//building a to do app

const Plus = () => {
  let [task, newTask] = useState("");
  let [savedTask , newsavedTask] = useState("");

  let txtArea = (event) => {
    newTask(event.target.value);
  };

  let sendData = (event) => {
    event.preventDefault();
    newsavedTask(task);
  }

  return (
    <>
      <div className="box">
        <form onSubmit = {sendData}>
          <h1>Enter Your Task Here</h1>
          <textarea
            value={task}
            onChange={txtArea}
            rows="10"
            cols="50"
            placeholder="Enter Your Task"
          ></textarea>
          <br />
          <br />
          <input type = "submit" value = "Save"/>

          <ol>
            <li>{savedTask}</li>
          </ol>
        </form>
      </div>
    </>
  );
};

export default Plus;
