import React from "react";

function Card(props) {
  return (
    <div className="cards">
      <div className="card">
        <img src={props.image}></img>
        <span>
          <h3>A Netflix Original Series</h3>
          <h5>{Name}</h5>
          <a href={props.link} target = "_blank">
            <button>Watch Now</button>
          </a>
        </span>
      </div>
    </div>
  );
}

export default Card;
