import React, { useState } from "react";
import CloneHeader from "./CloneHeader";
import CloneNote from "./CloneNote";
import CloneDisp from "./CloneDisp";
import "../node_modules/bootstrap/dist/css/bootstrap.min.css";

const Clone = () => {
    let [newNote , setNewNote] = useState([]);

    let frwrdData = (data) => {
        setNewNote((preData) => {
            return [...preData , data];
        })
    }

  return (
    <>
      <CloneHeader />
      <CloneNote onSelect = {frwrdData}/>
      {newNote.map((values ) => {
          return <CloneDisp onSelect = {frwrdData} itemTitle = {values.title} itemContent = {values.content} /> 
      })}
    </>
  );
};

export default Clone;
